import { dataActions } from "./data";

export const fetchImages = () => {
  return async (dispatch) => {
    const response = await fetch("http://localhost:8000/api/v1/getImages");
    const data = await response.json();
    dispatch(dataActions.replaceImages(data.data));
  };
};

export const updateLikeCount = (payload) => {
  return async (dispatch) => {
    const response = await fetch("/api/v1/likecount", {
      method: "Patch",
      headers: {
        "Content-type": "application/json",
      },

      body: JSON.stringify({ id: payload.id, likeCount: payload.likeCount }),
    });
    const data = await response.json();

    console.log(data.success);

    dispatch(
      dataActions.like({ id: payload.id, likeCount: payload.likeCount })
    );
  };
};
