import React from "react";
import classes from "./ImageComponent.module.css";
import { useState } from "react";
const ImageComponent = (props) => {
  let like = props.data.likeCount;
  let dislike = props.data.dislikeCount;



  const changeLikeHandler = () => {

    props.updateData({...props.data,likeCount:like+1});
  };
  const changeDislikeHandler = () => {
    props.updateData({...props.data,dislikeCount:dislike+1});
  };

  return (
    <div className={classes.card}>
      <p>{props.data.id}</p>
      <p>{props.data.name}</p>
      <img src={props.data.img} height="259" width="250"></img>
      <button className={classes.green_btn} onClick={changeLikeHandler}>
        like
      </button>
      <button className={classes.red_btn} onClick={changeDislikeHandler}>
        dislike
      </button>
      <div>
        <button className={classes.btn}>{like}</button>
        <button className={classes.btn}>{dislike}</button>
      </div>
    </div>
  );
};

export default ImageComponent;
